const {log} = require('../../index');

describe('suite test logs utils library', function () {

    const object = {
        elementDelimiter: '*',
        endOfLine: '\n',
        format: true,
        segmentTerminator: '~',
        subElementDelimiter: '>'
    };

    it('should print all logs', function () {
        expect(log.error(object)).toHaveProperty('levels');
        expect(log.debug(object)).toHaveProperty('levels');
        expect(log.info(object)).toHaveProperty('levels');
        expect(log.warn(object)).toHaveProperty('levels');
    });

});
